/* eslint-disable */
import { createStore } from 'vuex'
import api from '../api'

export default createStore({
  state: {
    user: (() => {
      try {
        const user = localStorage.getItem('user');
        return user ? JSON.parse(user) : null;
      } catch (e) {
        console.error('Error parsing user from localStorage:', e);
        return null;
      }
    })(),
    token: localStorage.getItem('token') || null,
    cart: (() => {
      try {
        const cart = localStorage.getItem('cart');
        return cart ? JSON.parse(cart) : [];
      } catch (e) {
        console.error('Error parsing cart from localStorage:', e);
        return [];
      }
    })(),
    cartCount: 0,
    categories: [],
    categoryData: {}
  },
  getters: {
    isLoggedIn: state => !!state.token,
    currentUser: state => state.user,
    cartItems: state => state.cart,
    cartTotal: state => {
      return state.cart.reduce((total, item) => {
        return total + (item.price * item.quantity)
      }, 0)
    },
    cartCount: state => {
      if (!state.cart || !Array.isArray(state.cart)) {
        return 0;
      }
      return state.cart.reduce((count, item) => {
        return count + (item.quantity || 0)
      }, 0)
    },
    allCategories: state => state.categories,
    getCategoryData: state => categoryId => state.categoryData[categoryId]
  },
  mutations: {
    setUser(state, user) {
      state.user = user
      localStorage.setItem('user', JSON.stringify(user))
    },
    setToken(state, token) {
      state.token = token
      localStorage.setItem('token', token)
    },
    logout(state) {
      state.user = null
      state.token = null
      localStorage.removeItem('user')
      localStorage.removeItem('token')
    },
    addToCart(state, item) {
      const existingItem = state.cart.find(cartItem => cartItem.id === item.id)
      
      if (existingItem) {
        existingItem.quantity += item.quantity
      } else {
        state.cart.push(item)
      }
      
      // 更新本地存储
      localStorage.setItem('cart', JSON.stringify(state.cart))
    },
    updateCartItem(state, { id, quantity }) {
      const item = state.cart.find(item => item.id === id)
      if (item) {
        item.quantity = quantity
      }
      localStorage.setItem('cart', JSON.stringify(state.cart))
    },
    removeFromCart(state, id) {
      state.cart = state.cart.filter(item => item.id !== id)
      localStorage.setItem('cart', JSON.stringify(state.cart))
    },
    clearCart(state) {
      state.cart = []
      localStorage.setItem('cart', JSON.stringify(state.cart))
    },
    setCategories(state, categories) {
      // 确保categories始终是数组
      state.categories = Array.isArray(categories) ? categories : 
                        (categories && categories.length ? categories : [])
    },
    setCategoryData(state, { categoryId, data }) {
      state.categoryData = {
        ...state.categoryData,
        [categoryId]: data
      }
    }
  },
  actions: {
    // 登录
    async login({ commit }, credentials) {
      try {
        const response = await api.user.login(credentials)
        if (response.code === 200) {
          commit('setUser', response.data.user)
          commit('setToken', response.data.token)
          return Promise.resolve(response.data)
        } else {
          return Promise.reject(new Error(response.message || '登录失败'))
        }
      } catch (error) {
        console.error('登录失败:', error)
        return Promise.reject(error)
      }
    },
    
    // 注册
    async register({ commit }, userData) {
      try {
        const response = await api.user.register(userData)
        if (response.code === 200) {
          return Promise.resolve(response.data)
        } else {
          return Promise.reject(new Error(response.message || '注册失败'))
        }
      } catch (error) {
        console.error('注册失败:', error)
        return Promise.reject(error)
      }
    },
    
    // 获取用户信息
    async getUserInfo({ commit }) {
      try {
        const response = await api.user.getUserInfo()
        if (response.code === 200) {
          commit('setUser', response.data)
          return Promise.resolve(response.data)
        } else {
          return Promise.reject(new Error(response.message || '获取用户信息失败'))
        }
      } catch (error) {
        console.error('获取用户信息失败:', error)
        return Promise.reject(error)
      }
    },
    
    // 登出
    logout({ commit }) {
      commit('logout')
    },
    
    // 购物车相关
    addToCart({ commit }, product) {
      commit('addToCart', product)
    },
    updateCartItem({ commit }, payload) {
      commit('updateCartItem', payload)
    },
    removeFromCart({ commit }, id) {
      commit('removeFromCart', id)
    },
    clearCart({ commit }) {
      commit('clearCart')
    },
    
    // 分类相关
    async fetchCategories({ commit }) {
      try {
        const response = await api.product.getProductCategories()
        if (response && response.code === 200) {
          // 确保数据是数组格式
          const categoriesData = response.data || []
          const categories = Array.isArray(categoriesData) ? categoriesData : 
                            (categoriesData && typeof categoriesData === 'object' ? [categoriesData] : [])
          
          commit('setCategories', categories)
          return categories
        } else {
          console.error('Failed to fetch categories:', response?.message || 'Unknown error')
          
          // 使用默认数据
          const defaultCategories = [
            { id: 1, name: '海洋设备', image: '/images/categories/marine-equipment.png' },
            { id: 2, name: '潜水装备', image: '/images/categories/diving-gear.png' },
            { id: 3, name: '海滩用品', image: '/images/categories/beach-accessories.png' },
          ]
          commit('setCategories', defaultCategories)
          return defaultCategories
        }
      } catch (error) {
        console.error('Failed to fetch categories:', error)
        
        // 使用默认数据
        const defaultCategories = [
          { id: 1, name: '海洋设备', image: '/images/categories/marine-equipment.png' },
          { id: 2, name: '潜水装备', image: '/images/categories/diving-gear.png' },
          { id: 3, name: '海滩用品', image: '/images/categories/beach-accessories.png' },
        ]
        commit('setCategories', defaultCategories)
        return defaultCategories
      }
    },
    async fetchCategoryData({ commit, state }, categoryId) {
      // 如果已经有数据，不重复获取
      if (state.categoryData[categoryId]) {
        return state.categoryData[categoryId]
      }

      try {
        // 使用API获取分类数据
        // const response = await api.product.getCategoryData(categoryId)
        // const data = response.data
        // commit('setCategoryData', { categoryId, data })
        // return data
        
        // 使用模拟数据
        await new Promise(resolve => setTimeout(resolve, 300)) // 模拟加载延迟
        
        // 根据不同分类ID返回不同的数据
        let data = {}
        
        switch(parseInt(categoryId)) {
          case 1: // 海洋设备
            data = {
              subcategories: [
                { id: 4, name: '导航工具', image: '/images/categories/navigation-tools.png' },
                { id: 5, name: '船只配件', image: '/images/categories/boat-accessories.png' }
              ],
              featuredProducts: [
                { id: 1, name: '专业航海指南针', price: 129.99, image: '/images/products/marine-compass.png' },
                { id: 2, name: '先进GPS导航仪', price: 349.99, image: '/images/products/gps-navigator.png' },
                { id: 3, name: '航海双筒望远镜', price: 199.99, image: '/images/products/marine-binoculars.png' },
                { id: 4, name: '高级船舶护舷', price: 89.99, image: '/images/products/boat-fenders.png' },
                { id: 5, name: '船用急救箱', price: 45.99, image: '/images/products/marine-first-aid.png' }
              ]
            }
            break
          case 2: // 潜水装备
            data = {
              subcategories: [
                { id: 6, name: '水肺装备', image: '/images/categories/scuba-equipment.png' },
                { id: 7, name: '浮潜装备', image: '/images/categories/snorkeling-gear.png' }
              ],
              featuredProducts: [
                { id: 6, name: '专业潜水调节器', price: 299.99, image: '/images/products/diving-regulator.png' },
                { id: 7, name: '潜水电脑表', price: 459.99, image: '/images/products/dive-computer.png' },
                { id: 8, name: '高级潜水面镜', price: 79.99, image: '/images/products/diving-mask.png' },
                { id: 9, name: '全脸浮潜面镜', price: 69.99, image: '/images/products/full-face-snorkel.png' },
                { id: 10, name: '碳纤维潜水蛙鞋', price: 149.99, image: '/images/products/dive-fins.png' }
              ]
            }
            break
          case 3: // 海滩用品
            data = {
              subcategories: [
                { id: 8, name: '海滩家具', image: '/images/categories/beach-furniture.png' },
                { id: 9, name: '防晒用品', image: '/images/categories/sun-protection.png' }
              ],
              featuredProducts: [
                { id: 11, name: '折叠海滩椅', price: 59.99, image: '/images/products/beach-chair.png' },
                { id: 12, name: '带沙锚的海滩伞', price: 49.99, image: '/images/products/beach-umbrella.png' },
                { id: 13, name: '防水海滩垫', price: 29.99, image: '/images/products/beach-blanket.png' },
                { id: 14, name: '珊瑚友好防晒霜SPF50', price: 24.99, image: '/images/products/reef-sunscreen.png' },
                { id: 15, name: '海滩遮阳帐篷', price: 79.99, image: '/images/products/sun-shelter.png' }
              ]
            }
            break
          default:
            data = {
              subcategories: [],
              featuredProducts: []
            }
        }
        
        commit('setCategoryData', { categoryId, data })
        return data
      } catch (error) {
        console.error(`Failed to fetch category data for category ${categoryId}:`, error)
        return null
      }
    }
  }
})
