/* eslint-disable */
<template>
  <div class="order-detail-container">
    <div class="order-detail-header">
      <h1>订单详情</h1>
    </div>
    
    <el-card class="order-detail-card" v-loading="loading">
      <!-- 订单状态 -->
      <div class="order-status">
        <div class="status-icon" :class="getStatusClass(order.status)">
          <i :class="getStatusIcon(order.status)"></i>
        </div>
        <div class="status-info">
          <div class="status-text">{{ getStatusText(order.status) }}</div>
          <div class="status-desc">{{ getStatusDescription(order.status) }}</div>
        </div>
      </div>
      
      <!-- 订单信息 -->
      <div class="order-info-section">
        <h3>订单信息</h3>
        <div class="info-container">
          <div class="info-item">
            <span class="label">订单编号:</span>
            <span class="value">{{ order.orderSn }}</span>
          </div>
          <div class="info-item">
            <span class="label">下单时间:</span>
            <span class="value">{{ formatDate(order.createTime) }}</span>
          </div>
          <div class="info-item">
            <span class="label">支付方式:</span>
            <span class="value">{{ getPaymentMethod(order.payType) }}</span>
          </div>
          <div class="info-item" v-if="order.paymentTime">
            <span class="label">支付时间:</span>
            <span class="value">{{ formatDate(order.paymentTime) }}</span>
          </div>
        </div>
      </div>
      
      <!-- 收货人信息 -->
      <div class="order-info-section">
        <h3>收货人信息</h3>
        <div class="info-container">
          <div class="info-item">
            <span class="label">收货人:</span>
            <span class="value">{{ order.receiverName }}</span>
          </div>
          <div class="info-item">
            <span class="label">联系电话:</span>
            <span class="value">{{ order.receiverPhone }}</span>
          </div>
          <div class="info-item">
            <span class="label">收货地址:</span>
            <span class="value">{{ formatAddress(order) }}</span>
          </div>
        </div>
      </div>
      
      <!-- 商品信息 -->
      <div class="order-info-section">
        <h3>商品信息</h3>
        <div class="product-list">
          <div v-for="item in order.orderItems" :key="item.id" class="product-item">
            <div class="product-img">
              <img :src="formatImageUrl(item.productPic)" alt="商品图片">
            </div>
            <div class="product-info">
              <div class="product-name">{{ item.productName }}</div>
              <div class="product-props" v-if="item.productProps">{{ item.productProps }}</div>
            </div>
            <div class="product-price">¥{{ (item.productPrice || 0).toFixed(2) }}</div>
            <div class="product-quantity">x {{ item.productQuantity }}</div>
            <div class="product-subtotal">¥{{ ((item.productPrice || 0) * (item.productQuantity || 1)).toFixed(2) }}</div>
          </div>
        </div>
      </div>
      
      <!-- 订单金额 -->
      <div class="order-info-section">
        <h3>订单金额</h3>
        <div class="price-container">
          <div class="price-item">
            <span class="label">商品总价:</span>
            <span class="value">¥{{ (order.totalAmount || 0).toFixed(2) }}</span>
          </div>
          <div class="price-item">
            <span class="label">运费:</span>
            <span class="value">¥{{ (order.freightAmount || 0).toFixed(2) }}</span>
          </div>
          <div class="price-item">
            <span class="label">优惠金额:</span>
            <span class="value">-¥{{ (order.discountAmount || 0).toFixed(2) }}</span>
          </div>
          <div class="price-item total">
            <span class="label">实付金额:</span>
            <span class="value">¥{{ (order.payAmount || 0).toFixed(2) }}</span>
          </div>
        </div>
      </div>
      
      <!-- 操作按钮 -->
      <div class="order-actions">
        <el-button v-if="order.status === 0" type="primary" @click="handlePay">去支付</el-button>
        <el-button v-if="order.status === 0" type="danger" @click="handleCancel">取消订单</el-button>
        <el-button v-if="order.status === 2" type="success" @click="handleConfirm">确认收货</el-button>
        <el-button v-if="order.status === 3" type="primary" @click="handleReview">评价</el-button>
        <el-button type="info" @click="goBack">返回</el-button>
      </div>
    </el-card>
  </div>
</template>

<script>
import { ref, reactive, onMounted } from 'vue'
import { useRoute, useRouter } from 'vue-router'
import { ElMessage, ElMessageBox } from 'element-plus'
import { http } from '@/utils/request'
import { staticBaseURL } from '@/utils/request'

export default {
  name: 'OrderDetail',
  setup() {
    const route = useRoute()
    const router = useRouter()
    
    const loading = ref(true)
    const order = ref({
      id: '',
      orderSn: '',
      status: 0,
      totalAmount: 0,
      payAmount: 0,
      freightAmount: 0,
      discountAmount: 0,
      payType: 0,
      createTime: '',
      paymentTime: '',
      receiverName: '',
      receiverPhone: '',
      receiverProvince: '',
      receiverCity: '',
      receiverRegion: '',
      receiverDetailAddress: '',
      orderItems: []
    })
    
    // 格式化图片URL
    const formatImageUrl = (path) => {
      if (!path) return '/images/placeholder.png';
      
      // 检查路径是否已经包含完整URL
      if (path.startsWith('http://') || path.startsWith('https://')) {
        return path;
      }
      
      // 处理相对路径，确保以/开头
      const normalizedPath = path.startsWith('/') ? path : `/${path}`;
      
      // 完整URL
      return `${staticBaseURL}${normalizedPath}`;
    };
    
    const fetchOrderDetail = async () => {
      try {
        loading.value = true
        
        // 从路由参数获取订单ID
        const orderId = route.query.orderId
        
        if (!orderId) {
          ElMessage.error('订单ID不能为空')
          router.push('/user/orders')
          return
        }
        
        // 获取订单详情
        const res = await http.get(`/order/detail/${orderId}`)
        
        if (res && res.code === 200 && res.data) {
          order.value = res.data
          
          // 确保订单项是数组
          if (!order.value.orderItems || !Array.isArray(order.value.orderItems)) {
            order.value.orderItems = []
          }
        } else {
          ElMessage.error('获取订单详情失败')
        }
        
        loading.value = false
      } catch (error) {
        console.error('获取订单详情出错:', error)
        ElMessage.error('网络错误，请稍后重试')
        loading.value = false
      }
    }
    
    const formatDate = (dateStr) => {
      if (!dateStr) return ''
      const date = new Date(dateStr)
      const year = date.getFullYear()
      const month = String(date.getMonth() + 1).padStart(2, '0')
      const day = String(date.getDate()).padStart(2, '0')
      const hour = String(date.getHours()).padStart(2, '0')
      const minute = String(date.getMinutes()).padStart(2, '0')
      const second = String(date.getSeconds()).padStart(2, '0')
      
      return `${year}-${month}-${day} ${hour}:${minute}:${second}`
    }
    
    const formatAddress = (order) => {
      const address = []
      if (order.receiverProvince) address.push(order.receiverProvince)
      if (order.receiverCity) address.push(order.receiverCity)
      if (order.receiverRegion) address.push(order.receiverRegion)
      if (order.receiverDetailAddress) address.push(order.receiverDetailAddress)
      
      return address.join(' ')
    }
    
    const getStatusText = (status) => {
      const statusMap = {
        0: '待付款',
        1: '待发货',
        2: '待收货',
        3: '已完成',
        4: '已取消'
      }
      return statusMap[status] || '未知状态'
    }
    
    const getStatusDescription = (status) => {
      const descMap = {
        0: '请在30分钟内完成支付，超时订单将自动取消',
        1: '商家已确认订单，正在备货中',
        2: '商品已发出，请注意查收',
        3: '订单已完成，感谢您的购买',
        4: '订单已取消'
      }
      return descMap[status] || ''
    }
    
    const getStatusClass = (status) => {
      const classMap = {
        0: 'status-waiting',
        1: 'status-processing',
        2: 'status-shipped',
        3: 'status-completed',
        4: 'status-cancelled'
      }
      return classMap[status] || ''
    }
    
    const getStatusIcon = (status) => {
      const iconMap = {
        0: 'el-icon-time',
        1: 'el-icon-loading',
        2: 'el-icon-truck',
        3: 'el-icon-circle-check',
        4: 'el-icon-circle-close'
      }
      return iconMap[status] || 'el-icon-info'
    }
    
    const getPaymentMethod = (payType) => {
      const methods = {
        1: '微信支付',
        2: '支付宝',
        3: '银联支付'
      }
      return methods[payType] || '未知支付方式'
    }
    
    const handlePay = () => {
      router.push({
        path: '/payment',
        query: {
          orderId: order.value.id,
          orderSn: order.value.orderSn
        }
      })
    }
    
    const handleCancel = () => {
      ElMessageBox.confirm('确定要取消该订单吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          const res = await http.post(`/order/cancel/${order.value.id}`)
          if (res && res.code === 200) {
            ElMessage.success('订单取消成功')
            fetchOrderDetail()
          } else {
            ElMessage.error(res.message || '订单取消失败')
          }
        } catch (error) {
          console.error('取消订单出错:', error)
          ElMessage.error('网络错误，请稍后重试')
        }
      }).catch(() => {
        // 用户取消操作
      })
    }
    
    const handleConfirm = () => {
      ElMessageBox.confirm('确认已收到商品？', '确认收货', {
        confirmButtonText: '确认收货',
        cancelButtonText: '取消',
        type: 'info'
      }).then(async () => {
        try {
          const res = await http.post(`/order/confirm/${order.value.id}`)
          if (res && res.code === 200) {
            ElMessage.success('确认收货成功')
            fetchOrderDetail()
          } else {
            ElMessage.error(res.message || '确认收货失败')
          }
        } catch (error) {
          console.error('确认收货出错:', error)
          ElMessage.error('网络错误，请稍后重试')
        }
      }).catch(() => {
        // 用户取消操作
      })
    }
    
    const handleReview = () => {
      router.push({
        path: '/review',
        query: {
          orderId: order.value.id,
          orderSn: order.value.orderSn
        }
      })
    }
    
    const goBack = () => {
      router.back()
    }
    
    onMounted(() => {
      fetchOrderDetail()
    })
    
    return {
      loading,
      order,
      formatImageUrl,
      formatDate,
      formatAddress,
      getStatusText,
      getStatusDescription,
      getStatusClass,
      getStatusIcon,
      getPaymentMethod,
      handlePay,
      handleCancel,
      handleConfirm,
      handleReview,
      goBack
    }
  }
}
</script>

