/* eslint-disable */
<template>
  <div class="user-orders">
    <el-card class="orders-card">
      <template #header>
        <div class="card-header">
          <h2>我的订单</h2>
          <el-select v-model="orderStatus" placeholder="订单状态" @change="handleStatusChange">
            <el-option label="全部订单" value=""></el-option>
            <el-option label="待付款" value="0"></el-option>
            <el-option label="待发货" value="1"></el-option>
            <el-option label="待收货" value="2"></el-option>
            <el-option label="已完成" value="3"></el-option>
            <el-option label="已取消" value="4"></el-option>
          </el-select>
        </div>
      </template>
      
      <!-- 订单列表 -->
      <div v-if="orders.length > 0">
        <div v-for="order in orders" :key="order.id" class="order-item">
          <div class="order-header">
            <div class="order-info">
              <span class="order-id">订单号: {{ order.orderNo }}</span>
              <span class="order-date">下单时间: {{ formatDate(order.createTime) }}</span>
            </div>
            <div class="order-status">
              <el-tag :type="getStatusType(order.status)">{{ getStatusText(order.status) }}</el-tag>
            </div>
          </div>
          
          <el-divider></el-divider>
          
          <div class="order-products">
            <div v-for="item in order.items" :key="item.id" class="product-item">
              <div class="product-img">
                <img :src="item.productImg" alt="商品图片">
              </div>
              <div class="product-info">
                <div class="product-name">{{ item.productName }}</div>
                <div class="product-props">{{ item.productProps }}</div>
              </div>
              <div class="product-price">¥{{ item.productPrice.toFixed(2) }}</div>
              <div class="product-quantity">x {{ item.quantity }}</div>
            </div>
          </div>
          
          <div class="order-footer">
            <div class="order-total">
              共 {{ order.totalNum }} 件商品，总计: <span class="price">¥{{ order.totalAmount.toFixed(2) }}</span>（含运费: ¥{{ order.freightAmount.toFixed(2) }}）
            </div>
            <div class="order-actions">
              <el-button v-if="order.status === 0" type="primary" size="small" @click="handlePay(order)">去支付</el-button>
              <el-button v-if="order.status === 0" type="danger" size="small" @click="handleCancel(order)">取消订单</el-button>
              <el-button v-if="order.status === 2" type="success" size="small" @click="handleConfirm(order)">确认收货</el-button>
              <el-button v-if="order.status === 3" type="primary" size="small" @click="handleReview(order)">评价</el-button>
              <el-button type="info" size="small" @click="viewOrderDetail(order)">查看详情</el-button>
            </div>
          </div>
        </div>
        
        <!-- 分页 -->
        <div class="pagination-container">
          <el-pagination
            background
            layout="prev, pager, next"
            :total="total"
            :page-size="pageSize"
            :current-page="currentPage"
            @current-change="handleCurrentChange"
          ></el-pagination>
        </div>
      </div>
      
      <!-- 空状态 -->
      <el-empty v-else description="暂无订单" :image-size="200"></el-empty>
    </el-card>
  </div>
</template>

<script>
import { ref, reactive, onMounted } from 'vue'
import { ElMessage, ElMessageBox } from 'element-plus'
import axios from 'axios'

export default {
  name: 'UserOrders',
  setup() {
    const orders = ref([])
    const orderStatus = ref('')
    const currentPage = ref(1)
    const pageSize = ref(5)
    const total = ref(0)
    
    const fetchOrders = async () => {
      try {
        const params = {
          pageNum: currentPage.value,
          pageSize: pageSize.value
        }
        
        if (orderStatus.value) {
          params.status = orderStatus.value
        }
        
        const response = await axios.get('/api/order/list', { params })
        if (response.data.code === 200) {
          orders.value = response.data.data.list
          total.value = response.data.data.total
        } else {
          ElMessage.error(response.data.message || '获取订单列表失败')
        }
      } catch (error) {
        console.error('获取订单列表出错:', error)
        ElMessage.error('网络错误，请稍后重试')
      }
    }
    
    const formatDate = (dateStr) => {
      if (!dateStr) return ''
      const date = new Date(dateStr)
      const year = date.getFullYear()
      const month = String(date.getMonth() + 1).padStart(2, '0')
      const day = String(date.getDate()).padStart(2, '0')
      const hour = String(date.getHours()).padStart(2, '0')
      const minute = String(date.getMinutes()).padStart(2, '0')
      
      return `${year}-${month}-${day} ${hour}:${minute}`
    }
    
    const getStatusText = (status) => {
      const statusMap = {
        0: '待付款',
        1: '待发货',
        2: '待收货',
        3: '已完成',
        4: '已取消'
      }
      return statusMap[status] || '未知状态'
    }
    
    const getStatusType = (status) => {
      const typeMap = {
        0: 'warning',
        1: 'primary',
        2: 'success',
        3: 'info',
        4: 'danger'
      }
      return typeMap[status] || 'info'
    }
    
    const handleStatusChange = () => {
      currentPage.value = 1
      fetchOrders()
    }
    
    const handleCurrentChange = (page) => {
      currentPage.value = page
      fetchOrders()
    }
    
    const handlePay = (order) => {
      // 跳转到支付页面
      ElMessage.info('即将跳转到支付页面，订单号: ' + order.orderNo)
      // 实际项目中应该跳转到支付页面
    }
    
    const handleCancel = (order) => {
      ElMessageBox.confirm('确定要取消该订单吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          const response = await axios.post(`/api/order/cancel/${order.id}`)
          if (response.data.code === 200) {
            ElMessage.success('订单取消成功')
            fetchOrders()
          } else {
            ElMessage.error(response.data.message || '订单取消失败')
          }
        } catch (error) {
          console.error('取消订单出错:', error)
          ElMessage.error('网络错误，请稍后重试')
        }
      }).catch(() => {
        // 用户取消操作
      })
    }
    
    const handleConfirm = (order) => {
      ElMessageBox.confirm('确认已收到商品？', '确认收货', {
        confirmButtonText: '确认收货',
        cancelButtonText: '取消',
        type: 'info'
      }).then(async () => {
        try {
          const response = await axios.post(`/api/order/confirm/${order.id}`)
          if (response.data.code === 200) {
            ElMessage.success('确认收货成功')
            fetchOrders()
          } else {
            ElMessage.error(response.data.message || '确认收货失败')
          }
        } catch (error) {
          console.error('确认收货出错:', error)
          ElMessage.error('网络错误，请稍后重试')
        }
      }).catch(() => {
        // 用户取消操作
      })
    }
    
    const handleReview = (order) => {
      // 跳转到评价页面
      ElMessage.info('即将跳转到评价页面')
      // 实际项目中应该跳转到评价页面或弹出评价对话框
    }
    
    const viewOrderDetail = (order) => {
      // 跳转到订单详情页
      ElMessage.info('即将跳转到订单详情页面')
      // 实际项目中应该跳转到订单详情页面
    }
    
    onMounted(() => {
      fetchOrders()
    })
    
    return {
      orders,
      orderStatus,
      currentPage,
      pageSize,
      total,
      formatDate,
      getStatusText,
      getStatusType,
      handleStatusChange,
      handleCurrentChange,
      handlePay,
      handleCancel,
      handleConfirm,
      handleReview,
      viewOrderDetail
    }
  }
}
